<?php//// Áàçîâûé êîíòðîëëåð ñàéòà.//abstract class C_Base extends C_Controller{	protected $title;		// çàãîëîâîê ñòðàíèöû	protected $content;		// ñîäåðæàíèå ñòðàíèöû    protected $keyWords;     protected function before(){		$this->title = 'Магазин ';		$this->content = '';		$this->keyWords="...";	}		//	// Ãåíåðàöèÿ áàçîâîãî øàáëîíàû	//		public function render()	{		$vars = array('title' => $this->title, 'content' => $this->content,'kw' => $this->keyWords);		$page = $this->Template('v/v_main.php', $vars);						echo $page;	}	}