<?php//// Áàçîâûé êëàññ êîíòðîëëåðà.//abstract class C_Controller{	// Ãåíåðàöèÿ âíåøíåãî øàáëîíà	protected abstract function render();		// Ôóíêöèÿ îòðàáàòûâàþùàÿ äî îñíîâíîãî ìåòîäà	protected abstract function before();		public function Request($action)	{		$this->before();//ìåòîä âûçûâàåòñÿ äî ôîðìèðîâàíèÿ äàííûõ äëÿ øàáëîí		$this->$action();   //$this->action_index		$this->render();	}		//	// Çàïðîñ ïðîèçâåäåí ìåòîäîì GET?	//	protected function IsGet()	{		return $_SERVER['REQUEST_METHOD'] == 'GET';	}	//	// Çàïðîñ ïðîèçâåäåí ìåòîäîì POST?	//	protected function IsPost()	{		return $_SERVER['REQUEST_METHOD'] == 'POST';	}	//	// Ãåíåðàöèÿ HTML øàáëîíà â ñòðîêó.	//	protected function Template($fileName, $vars = array())	{		// Óñòàíîâêà ïåðåìåííûõ äëÿ øàáëîíà.		foreach ($vars as $k => $v)		{			$$k = $v;		}		// вывод страницы.		ob_start();		include "$fileName";		return ob_get_clean();		}			// Åñëè âûçâàëè ìåòîä, êîòîðîãî íåò - çàâåðøàåì ðàáîòó	public function __call($name, $params){        die('неизвестный url!!!');	}}