<?phprequire_once "configs/config.php";function text_get(){	return file_get_contents('data/text.txt');}function text_set($text){	file_put_contents('data/text.txt', $text);}function changeImage($h, $w, $src, $newsrc, $type){    $newimg = imagecreatetruecolor($h, $w);    switch ($type) {        case 'jpeg':            $img = imagecreatefromjpeg($src);            imagecopyresampled($newimg, $img, 0, 0, 0, 0, $h, $w, imagesx($img), imagesy($img));            imagejpeg($newimg, $newsrc);            break;        case 'png':            $img = imagecreatefrompng($src);            imagecopyresampled($newimg, $img, 0, 0, 0, 0, $h, $w, imagesx($img), imagesy($img));            imagepng($newimg, $newsrc);            break;        case 'gif':            $img = imagecreatefromgif($src);            imagecopyresampled($newimg, $img, 0, 0, 0, 0, $h, $w, imagesx($img), imagesy($img));            imagegif($newimg, $newsrc);            break;    }};